  PRIMARY KEY (`id`) USING BTREE,
  INDEX `sessions_course_id_foreign_idx`(`course_id` ASC) USING BTREE,
  INDEX `sessions_q4game_id_foreign_idx`(`q4game_id` ASC) USING BTREE,
  INDEX `sessions_mediagame_id_foreign_idx`(`mediagame_id` ASC) USING BTREE,
  INDEX `sessions_phrasegame_id_foreign_idx`(`phrasegame_id` ASC) USING BTREE,
  CONSTRAINT `sessions_course_id_foreign_idx` FOREIGN KEY (`course_id`) REFERENCES `courses` (`id`) ON DELETE CASCADE ON UPDATE RESTRICT,
  CONSTRAINT `sessions_mediagame_id_foreign_idx` FOREIGN KEY (`mediagame_id`) REFERENCES `mediagames` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `sessions_phrasegame_id_foreign_idx` FOREIGN KEY (`phrasegame_id`) REFERENCES `phrasegames` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `sessions_q4game_id_foreign_idx` FOREIGN KEY (`q4game_id`) REFERENCES `q4games` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 39 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci ROW_FORMAT = Dynamic;

